theme.source:
@theme-name: t-audio-default;

.@{theme-name} {
    .w-audio-container {
        height:50px;
    }
    .w-audio-shadow-left {
        background: url(white.gif) no-repeat 0 2px;
    }
    .w-audio-shadow-right {
        background: url(white.gif) no-repeat 0 -113px;
    }
    .w-audio-container {
        background: transparent url(white.gif) repeat-x 0 -54px;
        color: #b5b5b5;
        font-size: 10px;
    }
    .w-audio-icon {
        background: url(white.gif) no-repeat;
    }
    .w-audio-bar {
        background-color: #fff;
    }
    .w-audio-percent {
        background-color: #00a0e9;
    }
    .w-audio-play {
        background-position: 0 -211px;
        &:hover {
            background-position: 0 -170px;
        }
    }

    .w-audio-pause {
        background-position: 0 -293px;
        &:hover {
            background-position: 0 -252px;
        }
    }

    .w-audio-volume-mute {
        background-position: 0 -375px;
        &:hover {
            background-position: 0 -334px;
        }
    }

    .w-audio-volume-normal {
        background-position: 0 -457px;
        &:hover {
            background-position: 0 -416px;
        }
    }
}

