@theme-name: t-slider-default;

.@{theme-name} {
  a:focus {
    outline: none;
  }

  .slidesjs {
    position: relative;
    margin: 0 auto;

    .slidesjs-navigation {
      position: absolute;
      width: 45px;
      height: 55px;
      background-image: url(images/sprite.png);
      top: 100px;
      text-indent: -9999em;
      z-index: 1000;
    }

    .slidesjs-previous {
      left: 10px;
      background-position: 0 -10px;
    }

    .slidesjs-next {
      right: 10px;
      background-position: 100% -10px;
    }

    .slidesjs-pagination {
      position: absolute;
      bottom: 20px;
      left: 100px;
      z-index: 1000;

      .slidesjs-pagination-item {
        list-style: none;
        display: inline-block;
        margin-right: 4px;
        float: none !important;
        float: left;

        a {
          display: inline-block !important;
          display: block;
          width: 50px;
          height: 6px;
          overflow: hidden;
          zoom: 1;
          background-color: #bbbbbb;
          text-indent: -9999em;
        }

        .active {
          background-color: #444444;
        }
      }
    }

    .slidesjs-slide {
      text-align: center;
      background: transparent url(images/loading.gif) no-repeat center center;

      img {
        vertical-align: middle;
      }
    }

    .sliderimgLoaded {
      background-image: none;
    }
  }
}

