@theme-name: t-slider-05;
.@{theme-name} {
  a:focus {
      outline: none;
  }
  .slidesjs {
    width: 600px;
    margin: 0 auto;

    * {
      margin: 0;
      padding: 0;
    }
    .ad-image-wrapper {
      width: 100%;
      height: 400px;
      margin-bottom: 10px;
      position: relative;
      overflow: hidden;

      .ad-loader {
        position: absolute;
        z-index: 10;
        top: 48%;
        left: 48%;
        border: 1px solid #CCC;
      }
      .ad-next {
        position: absolute;
        right: 0;
        top: 0;
        width: 25%;
        height: 100%;
        cursor: pointer;
        display: block;
        z-index: 100;
      }
      .ad-prev {
        position: absolute;
        left: 0;
        top: 0;
        width: 25%;
        height: 100%;
        cursor: pointer;
        display: block;
        z-index: 100;
      }
      .ad-prev, .ad-next {
        background: url(images/non-existing.jpg)~"\9"
      }
      .ad-prev .ad-prev-image, .ad-next .ad-next-image {
        background: url(images/ad_prev.png);
        width: 30px;
        height: 30px;
        display: none;
        position: absolute;
        top: 47%;
        left: 0;
        z-index: 101;
      }
      .ad-next .ad-next-image {
        background: url(images/ad_next.png);
        width: 30px;
        height: 30px;
        right: 0;
        left: auto;
      }
      .ad-image {
        position: absolute;
        overflow: hidden;
        top: 0;
        left: 0;
        z-index: 9;
        a {
          img {
            border: 0;
          }
        }
        .ad-image-description {
          position: absolute;
          bottom: 0px;
          left: 0px;
          padding: 7px;
          text-align: left;
          width: 100%;
          z-index: 2;
          background: url(images/opa75.png);
          color: #000;
        }
        }
    }

    .ad-controls {
      height: 20px;
    }
      .ad-info {
        float: left;
      }
      .ad-slideshow-controls {
        float: right;
      }
        .ad-slideshow-controls .ad-slideshow-start, .ad-slideshow-controls .ad-slideshow-stop {
          padding-left: 5px;
          cursor: pointer;
        }
        .ad-slideshow-controls .ad-slideshow-countdown {
          padding-left: 5px;
          font-size: 0.9em;
        }
      .ad-slideshow-running .ad-slideshow-start {
        cursor: default;
        font-style: italic;
      }
    .ad-nav {
      width: 100%;
      position: relative;
    }
      .ad-forward, .ad-back {
        position: absolute;
        top: 0;
        height: 100%;
        z-index: 10;
      }

      .ad-back {
        cursor: pointer;
        left: 10px;
        width: 13px;
        display: block;
        background: url(images/ad_scroll_back.png) 0px 22px no-repeat;
      }
      .ad-forward {
        cursor: pointer;
        display: block;
        right: 10px;
        width: 13px;
        background: url(images/ad_scroll_forward.png) 0px 22px no-repeat;
      }
      .ad-nav .ad-thumbs {
        overflow: hidden;
        margin: 0 30px;
      }
        .ad-thumbs .ad-thumb-list {
          float: left;
          width: 9000px;
          list-style: none;
        }
          .ad-thumbs li {
            float: left;
            padding-right: 5px;
          }
            .ad-thumbs li a {
              display: block;
            }
              .ad-thumbs li a img {
                border: 3px solid #CCC;
                display: block;
                height: 60px;
              }
              .ad-thumbs li a.ad-active img {
                border: 3px solid #000;
              }
  }

}

* {
  html {
    .@{theme-name} {
      .slidesjs {
          .ad-image-wrapper .ad-image .ad-image-description {
            background: none;
            filter:~"progid:DXImageTransform.Microsoft.AlphaImageLoader (enabled=true, sizingMethod=scale, src='opa75.png')";
          }
          .ad-forward,  .ad-back{
            height: 100px;
          }
      }
    }
  }
}

