@theme-name: t-slider-07;
.@{theme-name} {
	a:focus {
	    outline: none;
	}
	.expose * {
		margin: 0;
		padding: 0;
	}
	.expose {
		margin: 0 auto;
		overflow: hidden;
		position: relative;

		.exposeBar {
			float: left;
		}
		.exposeThumbs {
			width: 100px;
			background-color: transparent;
		}
		.exposeThumbs li {
			width: 90px;
			height: 60px;
			overflow: hidden;
			text-align: center;
			display: none;
			border: 5px solid #fff;
		}
		.exposeThumbs li.current {
			display: block;
		}
		.exposeThumbs li.active img {
			opacity: 1;
		}
		.exposeThumbs img {
			cursor: pointer;
			opacity: 0.3;
			-moz-transition: opacity 1s ease 0.1s;
			-webkit-transition: opacity 1s ease 0.1s;
			-o-transition: opacity 1s ease 0.1s;
			transition: opacity 1s ease 0.1s;
		}
		.exposeThumbs img:hover {
			-moz-transition: opacity 1s ease 0.1s;
			-webkit-transition: opacity 1s ease 0.1s;
			-o-transition: opacity 1s ease 0.1s;
			transition: opacity 1s ease 0.1s;
			opacity: 1;
