    margin: 0 auto;
    * {
      margin: 0;
      padding: 0;
    }
    a {
      display: block;
    }
    li {
      width: 480px;
      height: 320px;
      background-color: transparent;
      overflow: hidden;
      zoom:1;
      text-align: center;
    }
    li a {
      position: absolute;
      top: 0;
      left:0;
      bottom: 0;
      right: 0;
    }
    li a img {
      vertical-align: middle;
    }
    .roundabout-in-focus {
      cursor: pointer;
    }
  }
}
